前言 最近在做自动化安全扫描器开发，在项目中想引用比较知名的扫描器，如是整理了一下API作为参考 Nessus 6.x版本#!/usr/bin/env python # -*- coding: utf-8 -*- # @Time : 2017/8/21 下午4:08 # @Author : Yu BenLiu # @Site : QVQ # @File : nessus_api_6.py.py # @Software: PyCharm import sys # sys.path.append(&quot;..&quot;) import requests, json, csv, os, time from requests.packages.urllib3.exceptions import InsecureRequestWarning requests.packages.urllib3.disable_warnings(InsecureRequestWarning) # from core.settings import talscan_config,redis_task from host_type_check import * talscan_config = {&quot;report_filters&quot;: { &quot;awvs_white_list&quot;: [&quot;orange&quot;, &quot;red&quot;, &quot;blue&quot;], # green,blue,orange,red四种级别 &quot;nessus_white_list&quot;: [&quot;High&quot;, &quot;Medium&quot;, &quot;Low&quot;], &quot;bug_black_list&quot;: [ # 漏洞黑名单，过滤掉一些危害等级高，但没什么卵用的洞 &quot;User credentials are sent in clear text&quot; ] } } class Work(object): def __init__(self, scan_id=&quot;&quot;, scan_uuid=&quot;&quot;,scan_target=&quot;&quot;, scan_type=&quot;&quot;, scan_args=&quot;&quot;, back_fn=None): self.api_url = &#39;https://192.168.29.198:8834&#39; self.username = &quot;root&quot; self.password = &quot;ybl8651073&quot; self.filter = talscan_config[&quot;report_filters&quot;] self.report_save_dir = &#39;/tmp/&#39; self.verify = False self.token = &#39;&#39; self.enable = True self.scan_uuid=scan_uuid self.scan_id = scan_id self.target = scan_target self.scan_type = scan_type self.args = scan_args self.back_fn = back_fn self.result = {} def connect(self, method, resource, data=None, params=None): headers = {&#39;X-Cookie&#39;: &#39;token={0}&#39;.format(self.token), &#39;content-type&#39;: &#39;application/json&#39;} data = json.dumps(data) try: if method == &#39;POST&#39;: r = requests.post(str(self.api_url + resource), data=data, headers=headers, verify=self.verify) elif method == &#39;PUT&#39;: r = requests.put(str(self.api_url + resource), data=data, headers=headers, verify=self.verify) elif method == &#39;DELETE&#39;: r = requests.delete(str(self.api_url + resource), data=data, headers=headers, verify=self.verify) else: r = requests.get(str(self.api_url + resource), params=params, headers=headers, verify=self.verify) except Exception, e: print e return {&quot;status&quot;: 3} if r.status_code == 200: try: data = r.json() except: data = r.content result = {&quot;status&quot;: 1, &quot;data&quot;: data} return result else: result = {&quot;status&quot;: 3} return result def nessus_login(self): login = {&#39;username&#39;: self.username, &#39;password&#39;: self.password} data = self.connect(&#39;POST&#39;, &#39;/session&#39;, data=login) print data status = data[&quot;status&quot;] print status if status == 1: result = {&quot;status&quot;: 1, &quot;data&quot;: data[&quot;data&quot;][&#39;token&#39;]} print result return result else: result = {&quot;status&quot;: 0} print result return result def nessus_process_status(self, sid): # canceled,running,completed data = self.nessus_login() status = data[&quot;status&quot;] if status == 1: token = data[&quot;data&quot;] else: result = {&quot;status&quot;: 0} return result headers = {&#39;X-Cookie&#39;: &#39;token={0}&#39;.format(token), &#39;content-type&#39;: &#39;application/json&#39;} url = self.api_url + &#39;/scans/&#39; data = requests.get(url=url, params=None, headers=headers, verify=self.verify) res = data.json() info1=res[&#39;scans&#39;] print info1 try: return info1 except: return {&quot;status&quot;: 0} def nessus_policies(self,): data = self.nessus_login() status = data[&quot;status&quot;] if status == 1: token = data[&quot;data&quot;] else: result = {&quot;status&quot;: 0} return result headers = {&#39;X-Cookie&#39;: &#39;token={0}&#39;.format(token), &#39;content-type&#39;: &#39;application/json&#39;} url = self.api_url + &#39;/policies/&#39; data = requests.get(url=url, params=None, headers=headers, verify=self.verify) res = data.json() info=res[&#39;policies&#39;] print info try: return info except: return {&quot;status&quot;: 0} def nessus_add_task(self): if is_domain(self.target) or is_host(self.target): create_data = { &quot;uuid&quot;: self.scan_uuid, # &quot;uuid&quot;: &#39;ad629e16-03b6-8c1d-cef6-ef8c9dd3c658d24bd260ef5f9e66&#39;,#选择策略 &quot;settings&quot;: { &quot;name&quot;: self.scan_id, &quot;scanner_id&quot;: &quot;1&quot;, &quot;text_targets&quot;: self.target, &quot;enabled&quot;: False, &quot;launch_now&quot;: True, } } post_data = json.dumps(create_data) data = self.nessus_login() status = data[&quot;status&quot;] if status == 1: token = data[&quot;data&quot;] else: print &#39;99&#39; return {&quot;status&quot;: 2, &quot;data&quot;: &quot;NESSUS &gt;&gt;&gt;&gt; :登陆失败&quot;} headers = {&#39;X-Cookie&#39;: &#39;token={0}&#39;.format(token), &#39;content-type&#39;: &#39;application/json&#39;} r = requests.post(url=str(self.api_url + &#39;/scans&#39;), data=post_data, headers=headers, verify=self.verify) if r.status_code == 200: try: get_id = r.json() except: get_id = r.content sid = get_id[&#39;scan&#39;][&#39;id&#39;] result = {&quot;status&quot;: 1, &quot;data&quot;: sid} return result else: result = {&quot;status&quot;: 2, &quot;data&quot;: &quot;NESSUS &gt;&gt;&gt;&gt; :增加任务失败&quot;} return result else: return {&quot;status&quot;: 2, &quot;data&quot;: &quot;NESSUS &gt;&gt;&gt;&gt; :格式错误&quot;} def nessus_stop_task(self, sid): data = self.nessus_login() status = data[&quot;status&quot;] if status == 1: token = data[&quot;data&quot;] else: result = {&quot;status&quot;: 0} return result headers = {&#39;X-Cookie&#39;: &#39;token={0}&#39;.format(token), &#39;content-type&#39;: &#39;application/json&#39;} url = str(self.api_url + &#39;/scans/{0}/stop/&#39;.format(sid)) r = requests.post(url=url, params=None, headers=headers, verify=self.verify) if r.status_code == 200: result = {&quot;status&quot;: 1} return result else: result = {&quot;status&quot;: 0} return result def nessus_report_task(self, taskid, sid): bug_list = [] data = self.nessus_login() status = data[&quot;status&quot;] if status == 1: token = data[&quot;data&quot;] else: result = {&quot;status&quot;: 0} return result headers = {&#39;X-Cookie&#39;: &#39;token={0}&#39;.format(token), &#39;content-type&#39;: &#39;application/json&#39;} url = str(self.api_url + &#39;/scans/{0}/export&#39;.format(sid)) data = json.dumps({&quot;format&quot;: &quot;csv&quot;}) r = requests.post(url=url, data=data, headers=headers, verify=self.verify) try: file = r.json()[&#39;token&#39;] except: result = {&quot;status&quot;: 0} return result down_file_url = str(self.api_url + &#39;/scans/exports/{0}/download&#39;.format(file)) r = requests.get(url=down_file_url, headers=headers, verify=self.verify) csv_file = str(self.report_save_dir + &quot;{0}_nessus.csv&quot;.format(str(taskid))) f = open(csv_file, &#39;wb&#39;) data = r.content f.write(data) f.close() csv_open_file = open(csv_file, &#39;rb&#39;) csvReader = csv.reader(csv_open_file) for row in csvReader: parameterStr = &#39;,&#39;.join(row) parameters = parameterStr.split(&#39;,&#39;) PID = parameters[0] CVE = parameters[1] CVSS = parameters[2] Risk = parameters[3] Host = parameters[4] Protocol = parameters[5] Port = parameters[6] Name = parameters[7] Synopsis = parameters[8] Description = parameters[9] Solution = parameters[10] See_Also = parameters[11] Plugin_Output = parameters[12] bug_name = str(Name) bug_level = str(Risk) bug_summary = str(Synopsis) + &quot;
&quot; + str(Description) bug_detail = &quot;Bug Port : &quot; + str(Port) + &quot;
&quot; + &quot;CVE : &quot; + str(CVE) bug_repair = str(Solution) + &quot;
&quot; + str(Plugin_Output) if str(Risk) in self.filter[&#39;nessus_white_list&#39;]: bug_list.append( {&#39;bug_name&#39;: bug_name, &#39;bug_level&#39;: bug_level, &#39;bug_summary&#39;: bug_summary, &#39;bug_detail&#39;: bug_detail, &#39;bug_repair&#39;: bug_repair}) csv_open_file.close() # os.remove(csv_file) if len(bug_list) &gt; 0: result = {&quot;status&quot;: 1, &quot;data&quot;: bug_list} return bug_list else: result = {&quot;status&quot;: 0} return result def run(self): result = self.nessus_add_task() status = result[&quot;status&quot;] if status == 1: nessus_id = int(result[&quot;data&quot;]) while True: time.sleep(5) nessus_process = self.nessus_process_status(nessus_id) nessus_status = nessus_process[&quot;status&quot;] if nessus_status == 1: nessus_process_data = nessus_process[&quot;data&quot;].encode(&quot;utf8&quot;) if nessus_process_data == &quot;completed&quot;: break time.sleep(20) # 推迟20秒，获取报告;nessus任务进程到100有部分延迟结束时间 nessus_report = self.nessus_report_task(self.scan_id, nessus_id) nessus_report_status = nessus_report[&quot;status&quot;] if nessus_report_status == 1: nessus_report_data = nessus_report[&quot;data&quot;] data = [] for line in nessus_report_data: task_result = { &quot;scan_id&quot;: self.scan_id, &quot;model&quot;: &quot;nessus&quot;, &quot;bug_author&quot;: &quot;bing&quot;, &quot;bug_name&quot;: line[&quot;bug_name&quot;], &quot;bug_level&quot;: line[&quot;bug_level&quot;], &quot;bug_summary&quot;: line[&quot;bug_summary&quot;], &quot;bug_detail&quot;: line[&quot;bug_detail&quot;], &quot;bug_repair&quot;: line[&quot;bug_repair&quot;] } redis_task.sadd(&quot;nessus_result&quot;, task_result) print task_result # 任务最终结束 final_result = {&quot;status&quot;: 1, &quot;scan_id&quot;: self.scan_id, &quot;model&quot;: &quot;nessus&quot;} redis_task.sadd(&quot;nessus_result&quot;, final_result) print final_result else: # 任务最终结束 final_result = {&quot;status&quot;: 1, &quot;scan_id&quot;: self.scan_id, &quot;model&quot;: &quot;nessus&quot;} redis_task.sadd(&quot;nessus_result&quot;, final_result) print final_result elif status == 2: nessus_error = result[&quot;data&quot;] final_result = {&quot;status&quot;: 2, &quot;data&quot;: nessus_error, &quot;scan_id&quot;: self.scan_id, &quot;model&quot;: &quot;nessus&quot;} redis_task.sadd(&quot;nessus_result&quot;, final_result) print final_result #t = Work(&quot;1234455&quot;,&quot;ad629e16-03b6-8c1d-cef6-ef8c9dd3c658d24bd260ef5f9e66&quot;,&quot;www.baidu.com&quot;) #s = t.nessus_report_task(5,84) #print s Awvs 最新的api#!/usr/bin/env python # -*- coding: utf-8 -*- # @Time : 2017/8/22 下午3:08 # @Author : Yu BenLiu # @Site : QVQ # @File : awvs_api_11.py.py # @Software: PyCharm import json import requests import requests.packages.urllib3 &#39;&#39;&#39; import requests.packages.urllib3.util.ssl_ requests.packages.urllib3.util.ssl_.DEFAULT_CIPHERS = &#39;ALL&#39; or pip install requests[security] &#39;&#39;&#39; requests.packages.urllib3.disable_warnings() tarurl = &quot;https://192.168.29.207:3443/&quot; apikey = &quot;1986ad8c0a5b3df4d7028d5f3c06e936c38438d15a1fb4f4588e034d3c3490fef&quot; headers = {&quot;X-Auth&quot;: apikey, &quot;content-type&quot;: &quot;application/json&quot;} def addtask(url=&#39;&#39;): # 添加任务 data = {&quot;address&quot;: url, &quot;description&quot;: url, &quot;criticality&quot;: &quot;10&quot;} try: response = requests.post(tarurl + &quot;/api/v1/targets&quot;, data=json.dumps(data), headers=headers, timeout=30, verify=False) result = json.loads(response.content) return result[&#39;target_id&#39;] except Exception as e: print(str(e)) return def startscan(url): # 先获取全部的任务.避免重复 # 添加任务获取target_id # 开始扫描 &#39;&#39;&#39; 11111111-1111-1111-1111-111111111112 High Risk Vulnerabilities 11111111-1111-1111-1111-111111111115 Weak Passwords 11111111-1111-1111-1111-111111111117 Crawl Only 11111111-1111-1111-1111-111111111116 Cross-site Scripting Vulnerabilities 11111111-1111-1111-1111-111111111113 SQL Injection Vulnerabilities 11111111-1111-1111-1111-111111111118 quick_profile_2 0 {&quot;wvs&quot;: {&quot;profile&quot;: &quot;continuous_quick&quot;}} 11111111-1111-1111-1111-111111111114 quick_profile_1 0 {&quot;wvs&quot;: {&quot;profile&quot;: &quot;continuous_full&quot;}} 11111111-1111-1111-1111-111111111111 Full Scan 1 {&quot;wvs&quot;: {&quot;profile&quot;: &quot;Default&quot;}} &#39;&#39;&#39; targets = getscan() if url in targets: return &quot;repeat&quot; else: target_id = addtask(url) data = {&quot;target_id&quot;: target_id, &quot;profile_id&quot;: &quot;11111111-1111-1111-1111-111111111111&quot;, &quot;schedule&quot;: {&quot;disable&quot;: False, &quot;start_date&quot;: None, &quot;time_sensitive&quot;: False}} try: response = requests.post(tarurl + &quot;/api/v1/scans&quot;, data=json.dumps(data), headers=headers, timeout=30, verify=False) result = json.loads(response.content) return result[&#39;target_id&#39;] except Exception as e: print(str(e)) return def getstatus(scan_id): # 获取scan_id的扫描状况 try: response = requests.get(tarurl + &quot;/api/v1/scans/&quot; + str(scan_id), headers=headers, timeout=30, verify=False) result = json.loads(response.content) status = result[&#39;current_session&#39;][&#39;status&#39;] # 如果是completed 表示结束.可以生成报告 if status == &quot;completed&quot;: return getreports(scan_id) else: return result[&#39;current_session&#39;][&#39;status&#39;] except Exception as e: print(str(e)) return def delete_scan(scan_id): # 删除scan_id的扫描 try: response = requests.delete(tarurl + &quot;/api/v1/scans/&quot; + str(scan_id), headers=headers, timeout=30, verify=False) # 如果是204 表示删除成功 if response.status_code == &quot;204&quot;: return True else: return False except Exception as e: print(str(e)) return def delete_target(scan_id): # 删除scan_id的扫描 try: response = requests.delete(tarurl + &quot;/api/v1/targets/&quot; + str(scan_id), headers=headers, timeout=30, verify=False) # 如果是204 表示删除成功 if response.status_code == &quot;204&quot;: return True else: return False except Exception as e: print(str(e)) return def stop_scan(scan_id): # 停止scan_id的扫描 try: response = requests.post(tarurl + &quot;/api/v1/scans/&quot; + str(scan_id + &quot;/abort&quot;), headers=headers, timeout=30, verify=False) # 如果是204 表示停止成功 if response.status_code == &quot;204&quot;: return True else: return False except Exception as e: print(str(e)) return def scan_status(): # 停止scan_id的扫描 try: response = requests.get(tarurl + &quot;/api/v1/me/stats&quot;, headers=headers, timeout=30, verify=False) result = json.loads(response.content) print result return result except Exception as e: print(str(e)) return def getreports(scan_id): # 获取scan_id的扫描报告 &#39;&#39;&#39; 11111111-1111-1111-1111-111111111111 Developer 21111111-1111-1111-1111-111111111111 XML 11111111-1111-1111-1111-111111111119 OWASP Top 10 2013 11111111-1111-1111-1111-111111111112 Quick &#39;&#39;&#39; data = {&quot;template_id&quot;: &quot;11111111-1111-1111-1111-111111111111&quot;, &quot;source&quot;: {&quot;list_type&quot;: &quot;scans&quot;, &quot;id_list&quot;: [scan_id]}} try: response = requests.post(tarurl + &quot;/api/v1/reports&quot;, data=json.dumps(data), headers=headers, timeout=30, verify=False) result = response.headers report = result[&#39;Location&#39;].replace(&#39;/api/v1/reports/&#39;, &#39;/reports/download/&#39;) return tarurl.rstrip(&#39;/&#39;) + report except Exception as e: print(str(e)) return finally: delete_scan(scan_id) def config(url): target_id = addtask(url) # 获取全部的扫描状态 data = { &quot;excluded_paths&quot;: [&quot;manager&quot;, &quot;phpmyadmin&quot;, &quot;testphp&quot;], &quot;user_agent&quot;: &quot;Mozilla/5.0 (Macintosh; Intel Mac OS X 10_12_5) AppleWebKit/537.36 (KHTML, like Gecko) Chrome/59.0.3071.115 Safari/537.36&quot;, &quot;custom_headers&quot;: [&quot;Accept: */*&quot;, &quot;Referer:&quot; + url, &quot;Connection: Keep-alive&quot;], &quot;custom_cookies&quot;: [{&quot;url&quot;: url, &quot;cookie&quot;: &quot;UM_distinctid=15da1bb9287f05-022f43184eb5d5-30667808-fa000-15da1bb9288ba9; PHPSESSID=dj9vq5fso96hpbgkdd7ok9gc83&quot;}], &quot;scan_speed&quot;: &quot;moderate&quot;, # sequential/slow/moderate/fast more and more fast &quot;technologies&quot;: [&quot;PHP&quot;], # ASP,ASP.NET,PHP,Perl,Java/J2EE,ColdFusion/Jrun,Python,Rails,FrontPage,Node.js # 代理 &quot;proxy&quot;: { &quot;enabled&quot;: False, &quot;address&quot;: &quot;127.0.0.1&quot;, &quot;protocol&quot;: &quot;http&quot;, &quot;port&quot;: 8080, &quot;username&quot;: &quot;aaa&quot;, &quot;password&quot;: &quot;bbb&quot; }, # 无验证码登录 &quot;login&quot;: { &quot;kind&quot;: &quot;automatic&quot;, &quot;credentials&quot;: { &quot;enabled&quot;: False, &quot;username&quot;: &quot;test&quot;, &quot;password&quot;: &quot;test&quot; } }, # 401认证 &quot;authentication&quot;: { &quot;enabled&quot;: False, &quot;username&quot;: &quot;test&quot;, &quot;password&quot;: &quot;test&quot; } } try: res = requests.patch(tarurl + &quot;/api/v1/targets/&quot; + str(target_id) + &quot;/configuration&quot;, data=json.dumps(data), headers=headers, timeout=30 * 4, verify=False) data = {&quot;target_id&quot;: target_id, &quot;profile_id&quot;: &quot;11111111-1111-1111-1111-111111111111&quot;, &quot;schedule&quot;: {&quot;disable&quot;: False, &quot;start_date&quot;: None, &quot;time_sensitive&quot;: False}} try: response = requests.post(tarurl + &quot;/api/v1/scans&quot;, data=json.dumps(data), headers=headers, timeout=30, verify=False) result = json.loads(response.content) return result[&#39;target_id&#39;] except Exception as e: print(str(e)) return except Exception as e: raise e def getvulnerabilities(): # 停止scan_id的扫描 try: response = requests.get(tarurl + &quot;/api/v1/vulnerabilities&quot;, headers=headers, timeout=30, verify=False) result = json.loads(response.content) print result return result except Exception as e: print(str(e)) return def getscan(): # 获取全部的扫描状态 targets = [] try: response = requests.get(tarurl + &quot;/api/v1/scans&quot;, headers=headers, timeout=30, verify=False) results = json.loads(response.content) return results except Exception as e: raise e def getvulnerabilitiesinfo(sid): # 停止scan_id的扫描 try: response = requests.get(tarurl + &quot;/api/v1/vulnerabilities/&quot;+sid, headers=headers, timeout=30, verify=False) result = json.loads(response.content) print result return result except Exception as e: print(str(e)) return if __name__ == &#39;__main__&#39;: info=getscan() print info print type(info) #print getreports(&#39;f22d4aa1-e2de-4307-bd9d-ddf3aa531bc1&#39;,locals()) # print config(&#39;http://testhtml5.vulnweb.com/&#39;)
